const menuLinks=document.querySelectorAll(".header_menu-link[data-goto]");if(menuLinks.length>0){function onMenuLinkClick(e){const t=e.target;if(t.dataset.goto&&document.querySelector(t.dataset.goto)){const n=document.querySelector(t.dataset.goto).getBoundingClientRect().top+pageYOffset-document.querySelector("header").offsetHeight;window.scrollTo({top:n,behavior:"smooth"}),e.preventDefault()}}menuLinks.forEach((e=>{e.addEventListener("click",onMenuLinkClick)}))}let menuBtn=document.querySelector(".menu-btn"),menu=document.querySelector(".menu");menuBtn.addEventListener("click",(function(){menuBtn.classList.toggle("active"),menu.classList.toggle("active")}));